## PlotsExtra.py
## from the project results, plot all simulations for each of the s values

import matplotlib.pyplot as plt


# PLOT Recovered/beta ROWS/RANDOM arrangement
def plot_s(betas, mu1, mu2, mu3, s):
    plt.plot(betas, mu1[0], 'r-', label='μ=0.1 - Rows')      # rows
    plt.plot(betas, mu1[1], 'r:', label='μ=0.1 - Random')    # random
    plt.plot(betas, mu2[0], 'g-', label='μ=0.5 - Rows')
    plt.plot(betas, mu2[1], 'g:', label='μ=0.5 - Random')
    plt.plot(betas, mu3[0], 'b-', label='μ=0.9 - Rows')
    plt.plot(betas, mu3[1], 'b:', label='μ=0.9 - Random')
    plt.xlabel('β')
    plt.ylabel('R')
    plt.legend(loc=1)  # 'upper right'
    plt.title('SIR(s=%s; μ=0.1,0.5,0.9; p0=0.2)' % s)
    plt.savefig('Simulation_ALL_s' + s + '.png')
    plt.close()


